export const required = value => {
    if (value) {
        return undefined
    } else if (!value) {
        return "Field if required"
    }
}

export const maxLenghtCrater = (maxLenght) => value => {
    if (value.length > maxLenght) {
        return `Max Lenght ${maxLenght}`
    }
    return undefined

}
