const BUYITEM = "BUY-ITEM"
const LENGHT = "LENGHT"


let initialState = {
    item: [],
    lenght: null

};


const buyItem = (state = initialState, action) => {
    switch (action.type) {
        case BUYITEM: {


            let n = state.item.length;
            let buyeat = {
                _id: n,
                id: action.info.id,
                name: action.info.name,
                eat: action.info,
                cost: action.cost,
                size: action.size,
                finalcost: action.cost,
            }

            if (!state.item.some((buy) => buy.id === action.info.id)) {
                return {
                    ...state,
                    item: [...state.item, buyeat],
                    lenght: state.item.length

                }
            } else if (!state.item.some((buy) => buy.size === action.size)) {
                return {
                    ...state,
                    item: [...state.item, state.item.push(buyeat)],
                    lenght: state.item.length

                }

            } else {
                let searchId = action.info.id
                let index = state.item.findIndex(el => el.id === searchId)
                if (index > -1) {
                    if (state.item[index].size > 0) {
                        let searchSize = action.size
                        let finalindex = state.item.findIndex(el => el.size === searchSize)
                        return {
                            ...state,
                            ...state.item[finalindex].eat.amount += 1,
                            ...state.item[finalindex].cost = state.item[finalindex].finalcost * state.item[finalindex].eat.amount,
                        }
                    } else {
                        let searchCost = action.info.id
                        let finalinde = state.item.findIndex(el => el.id === searchCost)
                        return {
                            ...state,
                            ...state.item[finalinde].eat.amount += 1,
                            ...state.item[finalinde].cost = state.item[finalinde].finalcost * state.item[finalinde].eat.amount,
                        }
                    }
                }

            }

        }

        case LENGHT: {
            return {
                ...state,
                lenght: action.lenght
            }
        }


        default:
            return state;
    }
}


export const buy = (info, cost, size) => {
    return {
        type: BUYITEM,
        info, cost, size
    }
}

export const deleteLenght = (lenght) => {
    return {
        type: LENGHT,
        lenght
    }
}

export default buyItem;
