import s from './StylePizzaOrder/PizzaOrder.module.css'
import React from "react";
import {NavLink} from "react-router-dom";
import LastSeenContainer from "../../LaterSeen/LastSeenContainer";


class NewDrinkOrder extends React.Component {
    state = {
        cost: this.props.drink[this.props.number].cost,
        photoURL: this.props.drink[this.props.number].photoURL,
        name: this.props.drink[this.props.number].name,
        structure: this.props.drink[this.props.number].structure,
        size: this.props.size,
        ves: this.props.ves,


    }


    componentDidUpdate(prevProps, prevState) {
        if (prevProps != this.props) {
            this.setState({
                photoURL: this.props.drink[this.props.number].photoURL,
                name: this.props.drink[this.props.number].name,
                structure: this.props.drink[this.props.number].structure,
                cost: this.props.drink[this.props.number].cost,
                size: this.props.size,
                ves: this.props.ves,

            })
        }
    }


    render() {

        return (
            <div className={s.content}>
                <div className={s.container}>
                    <div className={s.photo_pizza}>
                        <img src={this.state.photoURL} alt="" height="400" width="400"/>
                    </div>
                    <div className={s.pizza_info}>
                        <div className={s.menu}>
                            <NavLink className={s.link_menu} to="/profile/pizza"> Меню/</NavLink><NavLink
                            className={s.link_pizza}
                            to="/profile/drink"> Напої/</NavLink>
                            <span className={s.link_name_pizza}>{this.state.name}</span>
                        </div>
                        <div className={s.pizza}>
                            <div className={s.name_pizza}>{this.state.name}</div>
                            <div className={s.cost_pizza}>{Math.round(this.state.cost)} грн</div>
                            <div className={s.strukture_pizza}>{this.state.structure}</div>
                        </div>
                    </div>
                    <LastSeenContainer/>

                </div>

            </div>
        )
    }
}


export default NewDrinkOrder
