};
const pizzaReducer = (state = initialState, action) => {
    switch (action.type) {
        case SETPIZZA: {
            return {
                ...state,
                pizza: [...state.pizza, ...action.pizza]
            }
        }

        default:
            return state;


    }


}


export const setpizzAC = (users) => {
    return {
        type: SETPIZZA,
        users: users,
    }
}

export default pizzaReducer;
