import {connect} from "react-redux";
import {Field, reduxForm} from "redux-form";
import {maxLenghtCrater, required} from "../../Validators/validator"
import s from "./CheckoutStyle/Checkout.module.css";
import {Input} from "../FormsControl/FormContorl";


const maxLengh = maxLenghtCrater(10)


const ChekoutForm = (props) => {

    return (
        <div className={s.container}>
            <div className={s.form}>
                <span>Оплата та доставка</span>
                <form onSubmit={props.handleSubmit}>
                    <div className={s.first}>
                        <div className={s.name}>
                            <span>Ім'я <span>*</span></span>
                            <Field placeholder={"Ім'я"} component={Input} validate={required}
                                   name={"name"}/>
                        </div>

                        <div className={s.phone}>
                            <span>Телефон<span>*</span></span>
                            <Field placeholder={"Телефон"} component={Input} validate={[required, maxLengh]}
                                   name={"phone"}/>
                        </div>
                    </div>

                    <div className={s.second}>
                        <span>Вулиця<span>*</span></span>
                        <Field placeholder={"Вулиця"} component={Input} validate={required}
                               name={"street"}/>
                    </div>

                    <div className={s.three}>
                        <div className={s.number_house}>
                            <span>Номер будинку<span>*</span></span>
                            <Field placeholder={"Номер будинку"} component={Input} validate={required}
                                   name={"street"}/>
                        </div>

                        <div className={s.pid}>
                            <span>Номер під'їзду <span>*</span></span>
                            <Field placeholder={"Номер під'їзду "} component={Input} validate={required}
                                   name={"pid"}/>
                        </div>

                    </div>

                    <div>
                        <button>vs</button>
                    </div>
                </form>
            </div>
            <div className={s.order}>
                <p>ВАШЕ ЗАМОВЛЕННЯ</p>
                <div className={s.order_info}>
                    <div className={s.information}>
                        <span>ТОВАР</span>
                        <span>ПРОМІЖНИЙ ПІДСУМОК</span>
                    </div>
                    <div>
                        {props.item.map(u => <div key={u._id}>

                            <div className={s.info}>

                                <div className={s.your_order}>
                                    <div className={s.container_name}>
                                        {u.eat.name} {u.size > 0 ? "-" : ""} {u.size} {u.size > 0 ? "см" : ""}


                                    </div>

                                    <div className={s.amount}>
                                        <div className={s.border_minus}>
                                            <p className={s.minus} onClick={this.plus = () => {
                                                u.eat.amount = u.eat.amount > 1 ? u.eat.amount - 1 : u.eat.amount
                                                u.cost = u.eat.cost * u.eat.amount
                                                this.forceUpdate()
                                            }}>-</p>
                                        </div>
                                        <p className={s.number_amount}>{u.eat.amount}</p>
                                        <p className={s.plus} onClick={this.plus = () => {
                                            u.eat.amount = u.eat.amount + 1
                                            u.cost = (u.eat.cost) * u.eat.amount
                                            this.forceUpdate()
                                        }}>+</p>
                                    </div>

                                    <div className={s.costs}>
                                        <span className={s.x}> {u.eat.amount} x</span> <span
                                        className={s.amount_cost}>{Math.round(u.eat.cost)} грн</span>
                                    </div>

                                </div>

                                <span className={s.delete} onClick={this.delet = () => {
                                    let searchindex = u._id
                                    this.state.item[this.state.item.findIndex(el => el._id === searchindex)].eat.amount = 1
                                    this.state.item.splice(this.state.item.findIndex(el => el._id === searchindex), 1)
                                    this.props.deleteLenght(this.props.lenght > 0 ? this.props.lenght - 1 : null)
                                    this.forceUpdate()
                                }
                                }>x</span>

                            </div>

                        </div>)}
                    </div>
                </div>
            </div>
        </div>
    )
}

const ReduxChekoutForm = reduxForm({
    form: "chekout"
})(ChekoutForm)


const Chekout = (props) => {
    const onSubmit = (formData) => {
        console.log(formData)
    }
    return (
        <div className={s.content}>
            <ReduxChekoutForm onSubmit={onSubmit} item={props.item}/>
        </div>
    )
}


let mapStateToprops = (state) => {

    return {
        item: state.BuyPage.item,


    }


}
export default connect(mapStateToprops, {})(Chekout)
