import {NavLink} from "react-router-dom";
import s from "./StyleBuy/Buy.module.css";


const Buy = (props) => {

    let view

    let cost = props.item.reduce(function ( b) {
        return  b.cost;
    }, 0);


    let size
    if (!props.item.size) {
        size = ""
    } else {
        size = <p> размер {props.item.size}</p>
    }

    if (props.item.length == 0) {
        view = <div className={s.container_no_buy}>
            <span>Ви ще не обрали замовлення</span>
            <NavLink to={"/profile/pizza"} className={s.p}>Перейти в меню</NavLink>
        </div>
    } else {
        view = <div>
            {props.item.map(u => <div key={u._id}>
                <NavLink className={s.container} to={"/later/" + u._id}>

                    <div className={s.container_photo}>
                        <img src={u.eat.photoURL} alt="" height="75" width="75"/>
                    </div>
                    <div className={s.info}>
                        <div className={s.container_name}>{u.eat.name}</div>
                        {size}
                    </div>
                </NavLink>
            </div>)}
            Цена
            {cost}


        </div>
    }


    return (
        <div className={s.content}>
            {view}

        </div>
    )
}

export default Buy
