import React, {Component} from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import {NavLink} from "react-router-dom";

export default class SliderItem extends Component {
    render() {
        const settings = {
            dots: false,
            infinite: true,
            speed: 200,
            slidesToShow: 1,
            slidesToScroll: 1,
            autoplay: true,
            arrows: false,


        };
        return (
            <div>
                <Slider {...settings}>
                    {this.props.pizza.map(u => <div key={u.id}>
                        <NavLink to={"/items/" + u.id}>

                            <div>
                                <img src={u.photoURL} alt="" height="75" width="75"/>
                            </div>
                            <div>
                                <div>{u.name}</div>
                                <div>Від {u.cost} грн</div>
                            </div>
                        </NavLink>


                    </div>)}

                </Slider>
            </div>
        )

    }
}
