import React from 'react';
import {connect} from "react-redux";
import {withRouter} from "react-router-dom"
import {compose} from "redux";
import PizzaOrder from "./PizzaOrder";
import {newItem} from "../../../Redux/later-seen-reducer";


class PizzaOrderContainer extends React.Component {


    render() {
        this.props.newItem(this.props.pizza[this.props.match.params.id - 1])

        return (
            <PizzaOrder pizza={this.props.pizza} number={this.props.match.params.id - 1}
                        size={this.props.size} ves={this.props.ves} newItem={this.props.newItem}/>
        )
    }
}


let mapStateToprops = (state) => {

    return {
        pizza: state.pizzaPage.pizza,
        size: state.pizzaPage.siza,
        ves: state.pizzaPage.ves,


    }


}

export default compose(
    connect(mapStateToprops, {newItem}),
    withRouter,
)(PizzaOrderContainer)
