const BUYITEM = "Buy-ITEM"

let initialState = {
    item: [],


};

const buyItem = (state = initialState, action) => {
    switch (action.type) {
        case BUYITEM: {

            let n = state.item.length - 1 + 1;
            let amount = state.item.amount;
            let buyeat = {
                _id: n,
                eat: action.info,
                cost: action.cost * amount,
                size: action.size,
                amount: 1

            }
            return {

                ...state,
                item: [...state.item, buyeat]
            }
        }


        default:
            return state;
    }
}


export const buy = (info, cost, size) => {

    return {
        type: BUYITEM,
        info, cost, size
    }
}

export default buyItem;
