import React from 'react';
import {setpizzAC} from "../../../Redux/pizza-reducer";
import {connect} from "react-redux";
import newPizza from "../../FooterCenter/NewPizzaContainer/newPizza";


let mapDispatchToProps = (dispatch) => {

    return {
        setUser: (users) => {
            dispatch(setpizzAC(users));
        },

    }

}

let mapStateToprops = (state) => {

    return {
        pizza: state.pizzaPage.pizza
    }


}

const PizzaContainer = connect(mapStateToprops, mapDispatchToProps)(newPizza);
export default PizzaContainer
