import {NavLink} from "react-router-dom";
import s from "./StyleBuy/Buy.module.css";
import {Component} from "react";

debugger

class Buy extends Component {

    componentDidUpdate(prevProps, prevState) {
        if (prevProps != this.props) {
            this.setState({
                item: this.props.item,
                amout: this.props.item[1].amout,

            })
        }
    }

    state = {
        item: this.props.item,
        amout: this.props.item[1].amout,

    }

    plus

    render() {


        let view

        let cost = this.state.item.reduce(function (a, b) {
            return Math.round(a + b.cost);
        }, 0);

        let plus

        if (this.state.item.length == 0) {
            view = <div className={s.container_no_buy}>
                <span>Ви ще не обрали замовлення</span>
                <NavLink to={"/profile/pizza"} className={s.p}>Перейти в меню</NavLink>
            </div>
        } else {
            view = <div>
                {this.props.item.map(u => <div key={u._id}>

                    <div className={s.container_photo}>
                        <img src={u.eat.photoURL} alt="" height="75" width="75"/>
                    </div>
                    <div className={s.info}>
                        <div className={s.container_name}>{u.eat.name}</div>
                        {u.size}
                        <div>
                            Цена {u.cost}
                        </div>
                        <div>
                            <p>-</p>
                            <p>{u.eat.amount}</p>
                            <p onClick={this.plus = () => {
                                this.setState({
                                    amout: this.state.item[u._id].eat.amount + 1,
                                })

                            }}>+</p>
                        </div>
                    </div>
                </div>)}
                Вмести
                {cost}


            </div>
        }


        return (
            <div className={s.content}>
                {view}

            </div>
        )
    }
}

export default Buy
