import React from 'react';
import {connect} from "react-redux";
import {withRouter} from "react-router-dom"
import {compose} from "redux";
import {newItem} from "../../../../Redux/later-seen-reducer";
import AllOrder from "./AllOrder";


class AllOrderContainer extends React.Component {


    render() {

        debugger
        let pizza, drink
        if (this.props.pizza[this.props.match.params.id - 1] < 14) {
            this.props.newItem(this.props.pizza[this.props.match.params.id - 1], this.props.ves, this.props.size)

            pizza = this.props.pizza[this.props.match.params.id - 1]
            drink = this.props.drink[0]


        } else if (this.props.drink[this.props.match.params.id - 1] >= 14 && this.props.drink[this.props.match.params.id - 1] < 20) {
            this.props.newItem(this.props.drink[this.props.match.params.id - 14])
            pizza = this.props.pizza[0]
            drink = this.props.drink[this.props.match.params.id - 1]
        }
        return (
            <AllOrder pizza={pizza} number={this.props.match.params.id - 1}
                      size={this.props.size} ves={this.props.ves} newItem={this.props.newItem}
                      drink={drink}/>
        )
    }
}


let mapStateToprops = (state) => {

    return {
        pizza: state.pizzaPage.pizza,
        size: state.pizzaPage.siza,
        ves: state.pizzaPage.ves,
        drink: state.drinkPage.drink,


    }


}

export default compose(
    connect(mapStateToprops, {newItem}),
    withRouter,
)(AllOrderContainer)
