import s from './FormControl.module.css';
import React from "react";


export const Input = ({input, meta, ...props}) => {
    const showError = meta.touched = false && meta.error

    return (
        <div>
            <div>
                <input {...input} {...props}/>
            </div>
            {showError && <span>{meta.error}</span>}
        </div>
    )

}
