import {combineReducers, createStore} from "redux";
import pizzaReducer from "./pizza-reducer";
import sneksReducer from "./sneks-reducer"
import drinkReducer from "./drink-reducer";
import sousReducer from "./sous-reducer";
import newPizzaReducer from "./new-pizza-reducer";
import newSnekReducer from "./new-snek-reducer";


let reducers = combineReducers({
    pizzaPage: pizzaReducer,
    sneksPage: sneksReducer,
    drinkPage: drinkReducer,
    sousPage: sousReducer,
    newPizzaPage: newPizzaReducer,
    newSnekPage: newSnekReducer
});

let store = createStore(reducers);

window.store = store
export default store;
