            name: "Coca Cola",
            cost: 25,
            structure: "",
            amount: 1

        },


    ],


};
const drinkReducer = (state = initialState, action) => {
    switch (action.type) {
        case SETDRINK: {
            return {
                ...state,
                drink: [...state.drink, ...action.drink]
            }
        }

        default:
            return state;


    }


}


export const setdrinkAC = (users) => {
    return {
        type: SETDRINK,
        users: users,
    }
}

export default drinkReducer;
