const BUYITEM = "BUY-ITEM"
const LENGHT = "LENGHT"


let initialState = {
    item: [],
    lenght: null

};


const buyItem = (state = initialState, action) => {
    switch (action.type) {
        case BUYITEM: {


            let n = state.item.length;
            let buyeat = {
                _id: n,
                id: action.info.id,
                name: action.info.name,
                eat: action.info,
                cost: action.cost,
                size: action.size,
                finalcost: action.cost,
            }

            if (!state.item.some((buy) => buy.id === action.info.id)) {
                return {
                    ...state,
                    item: [...state.item, buyeat],
                    lenght: state.item.length

                }
            } else if (!state.item.some((buy) => buy.cost === action.cost)) {
                return {
                    ...state,
                    item: [...state.item, buyeat],
                    lenght: state.item.length

                }

            } else {
                let searchId = action.info.id
                let index = state.item.findIndex(el => el.id === searchId)
                if (index > -1) {
                    let searchCost = action.cost
                    let finalindex = state.item.findIndex(el => el.cost === searchCost)
                    return {
                        ...state,
                        ...state.item[finalindex].eat.amount += 1,
                        ...state.item[finalindex].finalcost * state.item[finalindex].eat.amount + 1,

                    }
                }

            }

        }

        case LENGHT: {
            return {
                ...state,
                lenght: action.lenght
            }
        }


        default:
            return state;
    }
}


export const buy = (info, cost, size) => {
    return {
        type: BUYITEM,
        info, cost, size
    }
}

export const deleteLenght = (lenght) => {
    return {
        type: LENGHT,
        lenght
    }
}

export default buyItem;
