const NEWITEM = "NEW-ITEM"

let initialState = {
    item: [],


};

const laterSeen = (state = initialState, action) => {
    switch (action.type) {
        case NEWITEM: {

            let neweat = {
                eat: action.item,
            }

            if (state.item.lenght > 2) {
                delete state.item[2]
                return {

                    ...state,
                    item: [...state.item, neweat]
                }
            } else {
                return {

                    ...state,
                    item: [...state.item, neweat]
                }
            }


        }

        default:
            return state;

    }

}

export const newItem = (item) => {
    return {
        type: NEWITEM,
        item,
    }
}

export default laterSeen;
