const BUYITEM = "BUY-ITEM"


let initialState = {
    item: [],
    lenght: null

};


const buyItem = (state = initialState, action) => {
    switch (action.type) {
        case BUYITEM: {


            let n = state.item.length;


            function checkAndAdd(id) {
                var found = action.item.some(function (el) {
                    return el.id === id;
                });
                if (!found) {
                    let buyeat = {
                        _id: n,
                        eat: action.info,
                        cost: action.cost,
                        size: action.size,
                    }
                    return {


                        ...state,
                        item: [...state.item, buyeat],
                        lenght: state.item.length
                    }
                }
            }

            return {
                checkAndAdd
            }


        }


        default:
            return state;
    }
}


export const buy = (info, cost, size) => {
    return {
        type: BUYITEM,
        info, cost, size
    }
}


export default buyItem;
