const BUYITEM = "BUY-ITEM"
const LENGHT = "LENGHT"


let initialState = {
    item: [],
    lenght: null

};


const buyItem = (state = initialState, action) => {
    switch (action.type) {
        case BUYITEM: {


            let n = state.item.length;
            let buyeat
            if (!state.item.some((buy) => buy.name && buy.cost === action.info.name && action.cost)) {


                buyeat = {
                    _id: n,
                    eat: action.info,
                    cost: action.cost,
                    size: action.size,
                }


                return {
                    ...state,
                    item: [...state.item, buyeat],
                    lenght: state.item.length
                }
            }

        }

        case LENGHT: {
            return {
                ...state,
                lenght: action.lenght
            }
        }


        default:
            return state;
    }
}


export const buy = (info, cost, size) => {
    return {
        type: BUYITEM,
        info, cost, size
    }
}

export const deleteLenght = (lenght) => {
    return {
        type: LENGHT,
        lenght
    }
}

export default buyItem;
