import {NavLink} from "react-router-dom";
import s from "./StyleBuy/Buy.module.css";
import {Component} from "react";


class Buy extends Component {


    componentDidUpdate(prevProps, prevState) {
        if (prevProps != this.props) {
            this.setState({
                item: this.props.item,
            })
        }
    }

    state = {
        item: this.props.item,
        amout: 1,

    }


    plus
    minus

    render() {


        let view


        let cost = this.state.item.reduce(function (a, b) {
            return Math.round(a + b.cost);
        }, 0);


        if (this.state.item.length == 0) {
            view = <div className={s.container_no_buy}>
                <span>Ви ще не обрали замовлення</span>
                <NavLink to={"/profile/pizza"} className={s.p}>Перейти в меню</NavLink>
            </div>
        } else {
            view = <div className={s.container_buy}>
                <div className={s.order}>
                    <span>Ваше замовлення:</span>
                </div>
                {this.state.item.map(u => <div key={u._id}>

                    <div className={s.info}>

                        <div className={s.container_photo}>
                            <img src={u.eat.photoURL} alt="" height="75" width="75"/>
                        </div>

                        <div className={s.your_order}>
                            <div className={s.container_name}>
                                {u.eat.name} - {u.size} см
                            </div>

                            <div className={s.amount}>
                                <p className={s.minus} onClick={this.plus = () => {
                                    this.state.item[u._id].eat.amount = this.state.item[u._id].eat.amount > 1 ? this.state.item[u._id].eat.amount - 1 : this.state.item[u._id].eat.amount
                                    this.state.item[u._id].cost = this.state.item[u._id].eat.cost * this.state.item[u._id].eat.amount
                                    this.forceUpdate()
                                }}>-</p>
                                <p className={s.number_amount}>{u.eat.amount}</p>
                                <p className={s.plus} onClick={this.plus = () => {
                                    this.state.item[u._id].eat.amount = this.state.item[u._id].eat.amount + 1
                                    if(u.size==45){
                                        this.state.item[u._id].cost=this.state.item[u._id].cost*4
                                    }else if(u.size==33){
                                        this.state.item[u._id].cost=this.state.item[u._id].cost *2.7
                                    }else{
                                        this.state.item[u._id].cost=this.state.item[u._id].cost
                                    }

                                    this.state.item[u._id].cost = (this.state.item[u._id].eat.cost) * this.state.item[u._id].eat.amount
                                        this.forceUpdate()
                                }}>+</p>
                            </div>

                            <div className={s.costs}>
                                Цена {Math.round(u.cost)}
                            </div>

                        </div>

                    </div>

                </div>)}


                <div className={s.all}>
                    Разом: <span>{cost}</span>
                </div>

            </div>
        }


        return (
            <div className={s.content}>
                {view}

            </div>
        )
    }
}

export default Buy
