import {connect} from "react-redux";
import {Field, reduxForm} from "redux-form";
import {maxLenghtCrater, required} from "../../Validators/validator"
import s from "./CheckoutStyle/Checkout.module.css";
import {Input} from "../FormsControl/FormContorl";


const maxLengh = maxLenghtCrater(5)


const ChekoutForm = (props) => {

    return (
        <form onSubmit={props.handleSubmit}>
            <div>
                <Field placeholder={"Ім'я"} component={Input} validate={[required, maxLengh]} name={"name"}/>
            </div>

            <div>
                <Field placeholder={"Телефон"} component={Input} validate={[required, maxLengh]} name={"phone"}/>
            </div>


            <div>
                <button>vs</button>
            </div>
        </form>
    )
}

const ReduxChekoutForm = reduxForm({
    form: "chekout"
})(ChekoutForm)


const Chekout = (props) => {
    const onSubmit = (formData) => {
        console.log(formData)
    }
    return (
        <div>
            <ReduxChekoutForm onSubmit={onSubmit}/>
        </div>
    )
}


const mapStateToProps = (state) => ({})

export default connect(mapStateToProps, {})(Chekout)
