import React from 'react';
import {connect} from "react-redux";
import {withRouter} from "react-router-dom"
import {compose} from "redux";
import PizzaOrder from "./PizzaOrder";
import {NEW_SIZAAC} from "../../Redux/Menu/pizza-reducer";


class PizzaOrderContainer extends React.Component {


    render() {

        return (
            <PizzaOrder pizza={this.props.pizza} number={this.props.match.params.id - 1}
                        NEW_SIZAAC={this.props.NEW_SIZAAC } size={this.props.size}/>
        )
    }
}


let mapStateToprops = (state) => {

    return {
        pizza: state.pizzaPage.pizza,
        size:state.pizzaPage.siza
    }


}

export default compose(
    connect(mapStateToprops, {NEW_SIZAAC}),
    withRouter,
)(PizzaOrderContainer)
