const BUYITEM = "BUY-ITEM"
const LENGHT = "LENGHT"


let initialState = {
    item: [],
    lenght: null

};


const buyItem = (state = initialState, action) => {
    switch (action.type) {
        case BUYITEM: {


            let n = state.item.length;
            let buyeat = {
                _id: n,
                id: action.info.id,
                name: action.info.name,
                eat: action.info,
                cost: action.cost,
                size: action.size,
            }

            if (!state.item.some((buy) => buy.id === action.id) && !state.item.some((buy) => buy.cost === action.cost)) {

                return {
                    ...state,
                    item: [...state.item, buyeat],
                    lenght: state.item.length

                }


            }

        }

        case LENGHT: {
            return {
                ...state,
                lenght: action.lenght
            }
        }


        default:
            return state;
    }
}


export const buy = (info, cost, size) => {
    return {
        type: BUYITEM,
        info, cost, size
    }
}

export const deleteLenght = (lenght) => {
    return {
        type: LENGHT,
        lenght
    }
}

export default buyItem;
