import s from './StylePizzaOrder/PizzaOrder.module.css'
import React from "react";
import {NavLink} from "react-router-dom";


class PizzaOrder extends React.Component {
    state = {
        size: this.props.size,
        cost: this.props.pizza[this.props.number].cost,
        pizza: {
            photoURL: this.props.pizza[this.props.number].photoURL,
            name: this.props.pizza[this.props.number].name,
            structure: this.props.pizza[this.props.number].structure,
        }


    }
    NewSize = () => {
        this.setState({
            size: 33,
            cost: this.state.cost * 2,
        })


    }


    render() {

        return (
            <div className={s.content}>
                <div className={s.container}>
                    <div className={s.photo_pizza}>
                        <img src={this.state.pizza.photoURL} alt="" height="450" width="450"/>
                    </div>
                    <div className={s.pizza_info}>
                        <div className={s.menu}>
                            <NavLink className={s.link_menu} to="/profile/pizza"> Меню/</NavLink><NavLink
                            className={s.link_pizza}
                            to="/profile/pizza"> Пица/</NavLink>
                            <span className={s.link_name_pizza}>{this.state.pizza.name}</span>
                        </div>
                        <div className={s.pizza}>
                            <div className={s.name_pizza}>{this.state.pizza.name}</div>
                            <div className={s.cost_pizza}> Від {this.state.pizza.cost} грн</div>
                            <div className={s.strukture_pizza}>{this.state.pizza.structure}</div>
                        </div>

                    </div>

                    <div className={s.size_pizza}>
                        <div>
                            Розмір піци: {this.state.size}
                        </div>
                        <div onClick={this.NewSize}>
                            33
                        </div>

                    </div>
                </div>

            </div>
        )
    }
}


export default PizzaOrder
