const BUYITEM = "BUY-ITEM"
const UPDATEAMOUNT = "UPDATE-AMOUNT"

let initialState = {
    item: [],


};

const buyItem = (state = initialState, action) => {
    switch (action.type) {
        case BUYITEM: {

            let n = state.item.length;
            let buyeat = {
                _id: n,
                eat: action.info,
                cost: action.cost,
                size: action.size,

            }
            return {

                ...state,
                item: [...state.item, buyeat]
            }
        }

        case UPDATEAMOUNT: {
            debugger
            return {...state, item: [state.item, state.eat.amount + 1]}
        }


        default:
            return state;
    }
}


export const buy = (info, cost, size) => {
    return {
        type: BUYITEM,
        info, cost, size
    }
}


